mapper.select:
SELECT *
FROM client_sales
WHERE cli_nm = #{cliNm}
  AND yr_dt = #{yrDT}

mapper.update:
UPDATE client_sales
SET cnt       = #{cnt},
    suply_val = #{suplyVal}
WHERE yr_dt = #{yrDt}
  AND cli_nm = #{cliNm}

UPDATE client_sales
SET yr_dt = #{yrDt} cnt = #{cnt}, suply_val = #{suplyVal}
WHERE cli_nm = #{cliNm}
