( yr_dt
, mon_dt
, dy_dt
, unit
, val)
VALUES ( #{yrDt}
       , #{monDt}
       , #{dyDt}
       , #{unit}
       , #{val})
ON DUPLICATE KEY UPDATE yr_dt  = #{yrDt}
                      , mon_dt = #{monDt}
                      , dy_dt  = #{dyDt}

